import { Plugin } from 'vite';

interface UniKuRootOptions {
    /**
     * 是否启用虚拟节点
     * @default false
     */
    enabledVirtualHost?: boolean;
    /**
     * 是否启用全局ref
     * @default false
     */
    enabledGlobalRef?: boolean;
    /**
     * 根文件名
     * @default 'App.ku'
     */
    rootFileName?: string;
}
declare function UniKuRoot(options?: UniKuRootOptions): Plugin;

export { UniKuRoot as default };
